package com.lcvc.ebuy_test.web.backstage;import com.lcvc.ebuy_test.dao.AdminDao;import com.lcvc.ebuy_test.model.Admin;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpSession;@Controllerpublic class LoginLogouController {	AdminDao adminDao=new AdminDao();		@RequestMapping(value = "/backstage/login", method = RequestMethod.POST)	public String login(String username,String password,HttpSession session) {		Admin admin=adminDao.login(username, password);		if(admin!=null){			session.setAttribute("admin",admin);			return "redirect:/backstage/index";		}else{			return "/jsp/backstage/login.jsp";		}	}	@RequestMapping(value = "/backstage/index")	public String index() {		return "/jsp/backstage/main.jsp";	}}